import React, { useState, useEffect, useRef } from "react";
import Input from "../../components/UI/Input/Input";
import Button from "../../components/UI/Button/Button";
import classes from "./Auth.module.css";
import { connect } from "react-redux";
import Spinner from "../../components/UI/Spinner/Spinner";
import { register, logout, login } from "../../store/actions/auth";
import { Redirect } from "react-router-dom";

const auth = (props) => {
  const [signupMode, setSignupMode] = useState(false);
  const [error, setError] = useState("");
  const [test, setTest] = useState(null);
  const [controls, setControls] = useState({
    name: {
      elementType: "input",
      elementConfig: {
        type: "text",
        placeholder: "Full Name",
      },
      value: "",
      validation: {
        required: true,
        minLength: 1,
      },
      valid: false,
      touched: false,
    },
    email: {
      elementType: "input",
      elementConfig: {
        type: "email",
        placeholder: "Mail Address",
      },
      value: "",
      validation: {
        required: true,
        isEmail: true,
      },
      valid: false,
      touched: false,
    },
    password: {
      elementType: "input",
      elementConfig: {
        type: "password",
        placeholder: "Password",
      },
      value: "",
      validation: {
        required: true,
        minLength: 6,
      },
      valid: false,
      touched: false,
    },
  });

  const didMountRef = useRef();
  useEffect(() => {
    // Check if Logout pressed
    // if (props.isAuthenticated) {
    // props.onLogoutSubmit();
    // }

    props.onLogoutSubmit();
  }, []);

  useEffect(() => {
    if (didMountRef.current) {
      if (error !== props.error) {
        if (props.error.id === "REGISTER_FAIL") {
          setError(props.error);
            console.log("1:" + error)
        } else {
          setError(null);
          console.log("2:" + error)
        }
      }
      if (error !== props.error) {
        if (props.error.id === "LOGIN_FAIL") {
          setError(props.error);
          console.log("3:" + error)

        } else {
          setError(null);
          console.log("2:" + error)

        }
      } else {
        setError(null);
      }
    } else {
      didMountRef.current = true;
    }
  });

  const checkVaildity = (value, rules) => {
    let isValid = true;
    if (rules.required) {
      isValid = value.trim() !== "" && isValid;
    }
    if (rules.minLength) {
      isValid = value.length >= rules.minLength && isValid;
    }
    if (rules.maxLength) {
      isValid = value.length <= rules.maxLength && isValid;
    }
    return isValid;
  };

  const inputChangedHandler = (event, controlName) => {
    const updatedControls = {
      ...controls,
      [controlName]: {
        ...controls[controlName],
        value: event.target.value,
        valid: checkVaildity(
          event.target.value,
          controls[controlName].validation
        ),
        touched: true,
      },
    };
    // console.log(controls.password)
    setControls(updatedControls);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    if (signupMode) {
      const registrationInfo = {
        name: controls.name.value,
        email: controls.email.value,
        password: controls.password.value,
      };
      props.onRegisterSubmit(registrationInfo);
    } else if (!signupMode) {
      const loginInfo = {
        email: controls.email.value,
        password: controls.password.value,
      };
      props.onLoginSubmit(loginInfo);
    }
  };

  const switchAuthModeHandler = () => {
    setSignupMode(!signupMode);
  };

  const formElementsArray = [];
  for (let key in controls) {
    formElementsArray.push({
      id: key,
      config: controls[key],
    });
  }

  let form = formElementsArray.map((formElement) => {
    if (signupMode || !(formElement.id === "name")) {
      return (
        <Input
          key={formElement.id}
          elementType={formElement.config.elementType}
          elementConfig={formElement.config.elementConfig}
          value={formElement.config.value}
          invalid={!formElement.config.valid}
          shouldValidate={formElement.config.validation}
          touched={formElement.config.touched}
          changed={(event) => inputChangedHandler(event, formElement.id)}
        />
      );
    }
    return null;
  });

  if (props.isLoading) {
    form = <Spinner />;
  }

  let errorMessage = null;
  if (error) {
    errorMessage = <p>{error.msg.msg}</p>;
  }

  let redirectTo = <Redirect to="/" />;
  if (!props.isAuthenticated) {
    redirectTo = null;
  }

  console.log("test" + test)
  return (
    <div className={classes.Auth}>
      {/* Redirect when successfull register */}
      {redirectTo}

      {errorMessage}
      <form onSubmit={submitHandler}>
        {form}
        <Button btnType={"Success"}>SUBMIT</Button>
      </form>

      <Button btnType="Danger" clicked={switchAuthModeHandler}>
        SWITCH TO {signupMode ? "SIGNIN" : "SIGNUP"}
      </Button>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    isAuthenticated: state.auth.isAuthenticated,
    error: state.error,
    isLoading: state.auth.isLoading,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onRegisterSubmit: (registrationInfo) =>
      dispatch(register(registrationInfo)),
    onLogoutSubmit: () => dispatch(logout()),
    onLoginSubmit: (loginInfo) => dispatch(login(loginInfo)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(auth);
