import React from 'react';
import classes from './Modal.module.css';

const modal = (props) => (
    <div 
    className={classes.Modal}
    style={{
        transform: props.show ? 'translateY(100)' : 'translateY(-100vh)', 
        opacity: props.show ? '1': '0'            
    }}>
        {props.children}
    </div>
)

export default modal;
