    //     <Switch>
    //       <Route path="/" exact component={BurgerBuilder} />
    //       <Route path="/checkout" component={Checkout} />
    //       <Route path="/auth" component={Auth} />
    //       <Route path="/orders" component={Orders} />
    //     </Switch>
    //   </Layout>
    // </div>

    <Layout>
      <Suspense fallback={<p>Loading...</p>}>{routes}</Suspense>
    </Layout>
  );
};

export default app;
