import React, { Component } from "react";
import Button from "../../../components/UI/Button/Button";
import classes from "./ContactData.module.css";
import axios from "axios";
import Spinner from "../../../components/UI/Spinner/Spinner";
import Input from "../../../components/UI/Input/Input";

class ContactData extends Component {
    state = {
        orderForm: {
            name: {
                elementType: "input",
                elementConfig: {
                    type: "text",
                    placeholder: "Your Name",
                },
                value: "",
            },
            street: {
                elementType: "input",
                elementConfig: {
                    type: "text",
                    placeholder: "Street",
                },
                value: "",
            },
            zipCode: {
                elementType: "input",
                elementConfig: {
                    type: "text",
                    placeholder: "ZIP Code",
                },
                value: "",
            },
            countery: {
                elementType: "input",
                elementConfig: {
                    type: "text",
                    placeholder: "Country",
                },
                value: "",
            },
            email: {
                elementType: "input",
                elementConfig: {
                    type: "email",
                    placeholder: "Your Email",
                },
                value: "",
            },
            deliveryMethod: {
                elementType: "select",
                elementConfig: {
                    options: [
                        { value: "fastest", displayValue: "Fastest" },
                        { value: "cheapest", displayValue: "Cheapest" },
                    ],
                    placeholder: "ZIP Code",
                },
                value: "",
            },
        },
        loading: false,
    };
    componentDidMount() {
        console.log("[ContactData componentDidMount]");
    }
    componentDidUpdate() {
        console.log("[ContactData componentDidUpdate]");
    }
    orderHandler = (event) => {
        event.preventDefault();
        this.setState({ loading: true });

        const formData = {};
        for(let formElementIdentifier in this.state.orderForm){
            formData[formElementIdentifier] = this.state.orderForm[formElementIdentifier].value;
        }

        const ingredients = this.props.ingredients;
        const price = this.props.price;

        //   push new order

        const order = {
            ingredients: ingredients,
            price: price,
            name: this.state.name,
            email: this.state.email,
            address: {
                street: this.state.address.street,
                zipCode: this.state.address.postalCode,
            },
        };

        // alert(this.state.name)

        axios
            .post("http://localhost:5000/orders/add", order)
            .then((response) => {
                this.setState({ loading: false, purchasing: false });
                this.props.history.push("/");
            })
            .catch((error) => {
                this.setState({ loading: false, purchasing: false });
                console.log(error);
            });
    };

    inputChangedHandle = (event, inputIdentifier) => {
        const updatedOrderForm = {
            ...this.state.orderForm,
        };

        const updatedFormElement = {
            ...updatedOrderForm[inputIdentifier],
        };
        updatedFormElement.value = event.target.value;
        updatedOrderForm[inputIdentifier] = updatedFormElement;
        this.setState({ orderForm: updatedOrderForm });
    };
    render() {
        const formElementsArray = [];
        for (let key in this.state.orderForm) {
            formElementsArray.push({
                id: key,
                config: this.state.orderForm[key],
            });
        }

        let form = (
            <form onSubmit={this.orderHandler}>
                {formElementsArray.map((formElement) => {
                    return (
                        <Input
                            key={formElement.id}
                            elementType={formElement.config.elementType}
                            elementConfig={formElement.config.elementConfig}
                            value={formElement.config.value}
                            changed={(event) =>
                                this.inputChangedHandle(event, formElement.id)
                            }
                        />
                    );
                })}
            </form>
        );

        return <div className={classes.ContactData}>{form}</div>;
    }
}

export default ContactData;
