import * as actionTypes from './actions';

const initialState = {
    ingredients: {
        salad: 0,
        bacon: 0,
        cheese: 0,
        meat: 0,

    },
    totalPrice: 0,
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.ADD_INGREDIENTS:
            return {};
        case actionTypes.REMOVE_INGREDIENTS:
            return {};
        default:
            return state;   
    }
}

export default reducer;
