import React, { Component } from "react";
import Button from "../../../components/UI/Button/Button";
import classes from "./ContactData.module.css";
import axios from "axios";
import Spinner from "../../../components/UI/Spinner/Spinner";
import Input from "../../../components/UI/Input/Input";

class ContactData extends Component {
    state = {
        orderForm: {

        }
    };
    componentDidMount() {
        console.log('[ContactData componentDidMount]');
    }
    componentDidUpdate () {
        console.log('[ContactData componentDidUpdate]');
    }
    orderHandler = (event) => {
        event.preventDefault();
        this.setState({ ordered: true });

        const ingredients = this.props.ingredients;
        const price = this.props.price;

        const orderSchema = orderSchema;
        //   push new order

        const order = {
            ingredients: ingredients,
            price: price,
            name: this.state.name,
            email: this.state.email,
            address: {
                street: this.state.address.street,
                zipCode: this.state.address.postalCode,
            },
        };

        // alert(this.state.name)

        axios
            .post("http://localhost:5000/orders/add", order)
            .then((response) => {
                this.setState({ loading: false, purchasing: false });
                this.props.history.push("/");
            })
            .catch((error) => {
                this.setState({ loading: false, purchasing: false });
                console.log(error);
            });
    };

    render() {
        let ordered = (
            <div>
                <h4>Enter contact data</h4>
                <form>
                    <Input
                        inputtype="input"
                        type="text"
                        name="name"
                        placeholder="Your Name"
                    />
                    <Input
                        inputtype="input"
                        type="email"
                        name="email"
                        placeholder="Your Email"
                    />
                    <Input
                        inputtype="input"
                        type="text"
                        name="street"
                        placeholder="Street"
                    />
                    <Input
                        inputtype="input"
                        type="text"
                        name="postal"
                        placeholder="Postal"
                    />
                    <Button btnType="Success" clicked={this.orderHandler}>
                        ORDER
                    </Button>
                </form>
            </div>
        );

        if (this.state.ordered) {
            ordered = <Spinner />;
        }

        return <div className={classes.ContactData}>{ordered}</div>;
    }
}

export default ContactData;
