import React, { useState } from "react";
import Burger from "../../Burger/Burger";
import Button from "../../../components/UI/Button/Button";
import classes from "./CheckoutSummary.module.css";
import CheckoutForm from "../CheckoutForm/CheckoutForm";
import ContactData from "../../../containers/Checkout/ContactData/ContactData";

const checkoutSummary = (props) => {
    const [showContact, setShowContact] = useState(false);


    const contactOrSummaryHandler = () => {
      setShowContact(!showContact);

    }


    let contactOrSummary = (
        <CheckoutForm ingredients={props.ingredients} price={props.price} />
    );

      if(showContact) {
        contactOrSummary = <ContactData/>
      }


    return (
        <div className={classes.CheckoutSummary}>
            <Burger ingredients={props.ingredients} />

            <div className={classes.Form}>
                {contactOrSummary}
                <div>
                    <Button
                        btnType="Checkout_Cancle"
                        clicked={props.cancleClick}
                    >
                        CANCLE
                    </Button>

                    {/* <Link to={"/auth"}>
              btn
          </Link> */}
                    <Button
                        btnType="Checkout_Continue"
                        clicked={() => {props.continueClick(); contactOrSummaryHandler}}
                        disabled={props.hide}
                    >
                        CONTINUE
                    </Button>
                </div>
            </div>
        </div>
    );
};

export default checkoutSummary;
